#include <gtest/gtest.h>

#include <vector> // std::vector
#include <string> // std::string

#include "utils/aes/ecb.hpp"
#include "utils/base_conversions.hpp"
#include "utils/types.hpp" // byte, nibble


TEST(AesECB, decrypt)
{
    std::string ciphertext_str = "";
    std::string key_str = "YELLOW SUBMARINE";
    std::string expected_plaintext_str = "";

    std::vector<byte> expected_plaintext(expected_plaintext_str.begin(), expected_plaintext_str.end());

    std::vector<nibble> ciphertext_hex(ciphertext_str.begin(), ciphertext_str.end());
    std::vector<byte> ciphertext_data = base_conversions::hex_to_bytes(ciphertext_hex);
    std::vector<byte> key(key_str.begin(), key_str.end());
    std::vector<byte> plaintext_data = aes::ecb::decrypt(ciphertext_data, key, 128);
    std::vector<byte> plaintext_hex = base_conversions::bytes_to_hex(plaintext_data);

    EXPECT_EQ(plaintext_hex, expected_plaintext);
}

TEST(AesECB, encrypt)
{
    // TODO: should we remove padding?
    std::string plaintext_str = "";
    std::string key_str = "YELLOW SUBMARINE";
    std::string expected_ciphertext_str = "";

    std::vector<byte> expected_ciphertext(expected_ciphertext_str.begin(), expected_ciphertext_str.end());

    std::vector<nibble> plaintext_hex(plaintext_str.begin(), plaintext_str.end());
    std::vector<byte> plaintext_data = base_conversions::hex_to_bytes(plaintext_hex);
    std::vector<byte> key(key_str.begin(), key_str.end());
    std::vector<byte> ciphertext_data = aes::ecb::encrypt(plaintext_data, key, 128);
    std::vector<byte> ciphertext_hex = base_conversions::bytes_to_hex(ciphertext_data);
    EXPECT_EQ(ciphertext_hex, expected_ciphertext);
}
